import { PAGES } from '../constants';
import { setCurrentPage } from '../../features/game/gameSlice';
import { useDispatch, useSelector } from 'react-redux';
import { resetState } from '../../features/ui/uiSlice'; // Import the reset action creator
// import { Link } from 'react-router-dom'; // If using React Router

const defaultText = "Reset (Lose Progress)";
export const renderFooterButton = (currentPage) => {
    const dispatch = useDispatch();
    const handleReset = () => {
        dispatch(resetState()); // Dispatch the reset action
        // Additional logic if needed
        dispatch(setCurrentPage(PAGES.SPLASH));
    };

    switch (currentPage) {
        case PAGES.SPLASH:
            return null;
        case PAGES.INPUT:
            return <button onClick={handleReset}>{defaultText}</button>;
        case PAGES.MATCHUP:
            return <button onClick={handleReset}>{defaultText}</button>;
        case PAGES.RESULTS:
            return <button onClick={handleReset}>Rank Again!</button>;
        default:
            return <button onClick={handleReset}>The Ranking Game</button>;
    }
}
