const resultsMessage = "Rank Again!";

const Footer = () => {
    const dispatch = useDispatch();
    console.log(state.game.currentPage);

    const handleReset = () => {
        dispatch(resetState()); // Dispatch the reset action
        // Additional logic if needed
        dispatch(setCurrentPage(PAGES.SPLASH));
    };

    return (
        <footer className="footer">
            <p>Report a Bug</p>
            <button onClick={handleReset}>Reset -- Rank Again!</button> {/* For non-React Router approach */}
            {/* <Link to="/" onClick={handleReset}>Reset and Go to Start</Link> */} {/* For React Router approach */}
        </footer>
    );
};

export default Footer;
