import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { resetState } from '../../features/ui/uiSlice'; // Import the reset action creator
// import { Link } from 'react-router-dom'; // If using React Router
import { setCurrentPage } from '../../features/game/gameSlice';
import { PAGES, BUTTONS } from '../../features/constants';

const Footer = () => {
    const dispatch = useDispatch();
    const currentPage = useSelector((state) => state.game.currentPage);
    console.log(currentPage);
    const handleReset = () => {
        dispatch(resetState()); // Dispatch the reset action
        // Additional logic if needed
        dispatch(setCurrentPage(PAGES.SPLASH));
    };

    return (
        <footer className="footer">

            <button onClick={handleReset}>
                {currentPage === PAGES.RESULTS ? BUTTONS.RETRY : BUTTONS.DEFAULT}
                </button> {/* For non-React Router approach */}
            {/* <Link to="/" onClick={handleReset}>Reset and Go to Start</Link> */} {/* For React Router approach */}
            {/* <p>Report a Bug</p> */}
        </footer>
    );
};

export default Footer;
