// MatchupPage.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { startMatchup, nextPair } from '../../features/matchup/matchupSlice';
import { selectCurrentPair, selectIsComparisonComplete } from '../../selectors/matchupSelectors';
import { setCurrentPage } from '../../features/game/gameSlice';
import { MatchupView } from './MatchupView';
import { PAGES } from '../../features/constants';
import { selectChoice, incrementVote, selectTopic, selectItems } from '../../features/ui/uiSlice';

const MatchupPage = () => {
  const dispatch = useDispatch();
  const isComparisonComplete = useSelector(selectIsComparisonComplete);
  const currentPair = useSelector(selectCurrentPair);
  const topic = useSelector(selectTopic);
  const items = useSelector(selectItems);

  useEffect(() => {
    if (!items.length) {
      dispatch(startMatchup());
    }
  }, [dispatch, items]);

  useEffect(() => {
    if (isComparisonComplete) {
      dispatch(setCurrentPage(PAGES.RESULTS));
    }
  }, [isComparisonComplete, dispatch]);

  const handleChoiceSelect = (choiceIndex) => {
    if (currentPair && currentPair.length === 2) {
      const selectedItem = items[currentPair[choiceIndex]];
      if (selectedItem) {
        dispatch(incrementVote(selectedItem.id));
        dispatch(nextPair());
      }
    }
  };

  return (
    <MatchupView 
      topic={topic}
      handleChoiceSelect={handleChoiceSelect}
      currentPair={currentPair}
      items={items}
    />
  );
};

export default MatchupPage;
